-- INSERT DEFAULT IMAGE INTO PROFILE PICTURE TABLE
-- INSERT WITH ID = 1 ONLY IF ID = 1 DOES NOT EXIST
INSERT INTO profile_picture (profile_picture_id, profile_picture)
    SELECT 1, '\'
    WHERE NOT EXISTS(SELECT 1 FROM profile_picture WHERE profile_picture_id = 1);

-- Function to insert a new health insurance entry
CREATE OR REPLACE FUNCTION insert_health_insurance_entry(health_insurance_name varchar(100)) RETURNS void AS $$
DECLARE
    -- Declare a variable to store the health insurance name, because the parameter is not accessible inside select otherwise
    health_insurance_name_var varchar(100);
BEGIN
    health_insurance_name_var := health_insurance_name;
    INSERT INTO health_insurance(health_insurance_name)
        SELECT (health_insurance_name_var)
        WHERE NOT EXISTS(SELECT * FROM health_insurance h1 WHERE health_insurance_name_var = h1.health_insurance_name);
END;
$$ LANGUAGE plpgsql;

-- Using insert_health_insurance_entry function
SELECT insert_health_insurance_entry('Omint');
SELECT insert_health_insurance_entry('Osde');
SELECT insert_health_insurance_entry('Swiss medical');


